CREATE TABLE ebay_game (id BIGINT AUTO_INCREMENT, title VARCHAR(255), block_title TEXT, link VARCHAR(255), description text, current_price BIGINT, end_time DATETIME, bid_count BIGINT, game_hash VARCHAR(255), postage_packing_fee BIGINT, profile_name VARCHAR(255), profile_url VARCHAR(255), feedback_score BIGINT, title_processed tinyint(1), platform VARCHAR(20), slug VARCHAR(255), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, UNIQUE INDEX ebay_game_sluggable_idx (slug), PRIMARY KEY(id)) ENGINE = INNODB;
